INSERT INTO `producteurs` VALUES ('76', 'Le Grain Libre', '5 route du Coudray, 37600 Saint jean Saint Germain', 'Le Grain Libre, c\'est un collectif de sept fermes bio du Sud Touraine unies par une envie forte de vivre une aventure commune : apprendre à valoriser nos céréales et à les transformer ensemble.\r\n C\'est dans l\'atelier de Saint-Jean-Saint-Germain que les grains de blé tendre de variétés paysannes passent entre les meules de granite du moulin pour devenir des farines.\r\n Simplement mélangées à un peu d\'eau, c\'est l\'extrudeuse qui donne forme à nos spécialités céréalières, type pâtes sèches.', 'Agriculture biologique\r\nN° Siret :80982627400015 N° TVA intracom. :FR53809826274\r\nProduits : Céréale', '', null);
INSERT INTO `producteurs` VALUES ('77', 'Le goût des saisons', '2013 Route des Forêts, 18110 Saint-Martin-d\'Auxigny', 'Paysan maraîcher en agriculture biologique diversifiée. Je cultive sur la commune de St Martin d\'Auxigny et dans les marais de Bourges. Les cultures sont en places permanentes en agroécologie et un système verger - maraicher.', 'Agriculture biologique\r\nCertificat : Ecocert\r\nProduits : Fruit, Légume', '', null);
