INSERT INTO `producteurs` VALUES ('94', 'SAS Brasserie Sancerroise', '258 Route d\'Amigny, 18300 Sancerre', 'A la recherche d\'une bière artisanale à Sancerre ?\r\n Nos bières sont toutes brassées localement dans le Centre de la France à Sancerre, selon d\'anciennes recettes, toutes différentes les unes des autres, ainsi nous vous donnons le choix entre : \r\n Infusion et décoction, fermentation haute et basse, bières filtrées ou non filtrées, bières refermentées en bouteilles ou non.', 'Agriculture biologique\r\nCertificat : Bio\r\nProduits : Boisson', '', null);
